import groovy.json.JsonSlurper
import groovy.json.JsonBuilder
import groovy.json.JsonOutput

def responseContent = testRunner.testCase.getTestStepByName("get-transit _time").getPropertyValue("response")
def response = new JsonSlurper().parseText(responseContent)
log.info(response)

def lat = new JsonOutput().toJson(response.routes.bounds.northeast.lat.get(0));
log.info("40.728783"+"Latitude ="+" "+lat)

com.eviware.soapui.SoapUI.globalProperties.setPropertyValue('singlelatitude',lat)

def lng = new JsonOutput().toJson(response.routes.bounds.northeast.lng.get(0));
log.info ("-73.7897503"+"Longitude ="+" "+lng)

com.eviware.soapui.SoapUI.globalProperties.setPropertyValue('singlelongitude',lng)
